SELECT p2.p_personid, p2.p_lastname, p2.p_birthday, p2.p_creationdate, p2.p_gender, p2.p_browserused, p2.p_locationip, pl.pl_name FROM person p1, knows k1, knows k2, knows k3, person p2, place pl WHERE p1.p_personid = :personId AND p2.p_firstname = :firstName AND p1.p_personid = k1.k_person1id AND k1.k_person2id = k2.k_person1id AND k2.k_person2id = k3.k_person1id AND k3.k_person2id = p2.p_personid AND p2.p_placeid = pl.pl_placeid;
