SELECT f.p_personid, f.p_firstname, f.p_lastname FROM person p1, knows k, person f, comment c, post ps, post_tag pt, tag t, tagclass tc1, tagclass tc2 WHERE tc1.tc_subclassoftagclassid = tc2.tc_tagclassid AND t.t_tagclassid = tc1.tc_tagclassid AND pt.mt_tagid = t.t_tagid AND c.m_replyof_post = ps.m_messageid AND c.m_creatorid = f.p_personid AND ps.m_messageid = pt.mt_messageid AND k.k_person2id = f.p_personid AND p1.p_personid = :personId AND p1.p_personid = k.k_person1id AND tc2.tc_name = :tagClassName;
