SELECT p2.p_personid, p2.p_firstname, p2.p_lastname FROM person p1, knows k1, person p2, comment m1, place pl1, comment m2, place pl2 WHERE m1.m_creationdate >= :startDate AND m1.m_creationdate < :durationDays AND m2.m_creationdate >= :startDate AND m2.m_creationdate < :durationDays AND p1.p_personid = k1.k_person1id AND k1.k_person2id = p2.p_personid AND m2.m_creatorid = p2.p_personid AND m1.m_locationid = pl1.pl_placeid AND m1.m_creatorid = p2.p_personid AND m2.m_locationid = pl2.pl_placeid AND p1.p_personid = :personId AND pl1.pl_name = :countryXName AND pl2.pl_name = :countryYName;
