SELECT f.p_personid, f.p_firstname, f.p_lastname FROM person p1, knows k, person f, comment_hascreator chc, comment c, comment_replyof_post crp, post ps, post_tag pt, tag t, tag_hastype tht, tagclass tc1, issubclassof isc, tagclass tc2 WHERE isc.isc_tagclass1id = tc1.tc_tagclassid AND isc.isc_tagclass2id = tc2.tc_tagclassid AND t.t_tagid = tht.tht_tagid AND tht.tht_tagclassid = tc1.tc_tagclassid AND pt.mt_tagid = t.t_tagid AND ps.m_messageid = pt.mt_messageid AND c.m_messageid = crp.crp_message1id AND crp.crp_message2id = ps.m_messageid AND c.m_messageid = chc.chc_messageid AND chc.chc_personid = f.p_personid AND k.k_person2id = f.p_personid AND p1.p_personid = k.k_person1id AND p1.p_personid = $personId AND tc2.tc_name = $tagClassName;
