SELECT p2.p_personid, p2.p_firstname, p2.p_lastname FROM person p1, knows k1, knows k2, person p2, comment m1, comment_hascreator chc1, place pl1, comment_islocatedin ci1, comment m2, comment_hascreator chc2, place pl2, comment_islocatedin ci2 WHERE m1.m_creationdate >= $startDate AND m1.m_creationdate < $durationDays AND m2.m_creationdate >= $startDate AND m2.m_creationdate < $durationDays AND p1.p_personid = k1.k_person1id AND k2.k_person1id = k1.k_person2id AND k2.k_person2id = p2.p_personid AND p2.p_personid = chc1.chc_personid AND chc1.chc_messageid = m1.m_messageid AND m1.m_messageid = ci1.ci_messageid AND ci1.ci_placeid = pl1.pl_placeid AND p2.p_personid = chc2.chc_personid AND chc2.chc_messageid = m2.m_messageid AND m2.m_messageid = ci2.ci_messageid AND ci2.ci_placeid = pl2.pl_placeid AND p1.p_personid = $personId AND pl1.pl_name = $countryXName AND pl2.pl_name = $countryYName;

