SELECT f.f_title FROM person p1, knows k1, person p2, forum_person fp, forum f, containerof_post co, post m, post_hascreator phc WHERE f.f_forumid = co.co_forumid AND m.m_messageid = co.co_messageid AND fp.fp_forumid = f.f_forumid AND p2.p_personid = fp.fp_personid AND k1.k_person2id = p2.p_personid AND p1.p_personid = k1.k_person1id AND phc.phc_personid = p2.p_personid AND phc.phc_messageid = m.m_messageid AND p1.p_personid = $personId AND fp.fp_joindate >= $minDate;
