SELECT p2.p_personid, p2.p_firstname, p2.p_lastname, c.m_creationdate, c.m_messageid, c.m_content FROM person p1, post_hascreator phc, post ps, comment_replyof_post crp, comment c, comment_hascreator chc, person p2 WHERE c.m_messageid = chc.chc_messageid AND chc.chc_personid = p2.p_personid AND c.m_messageid = crp.crp_message1id AND crp.crp_message2id = ps.m_messageid AND p1.p_personid = phc.phc_personid AND phc.phc_messageid = ps.m_messageid AND p1.p_personid = $personId;
